import React, { useReducer, useState } from 'react';

import './App.css';


const formReducer = (state, event) => {

 return {

  ...state,

  [event.name]: event.value

 };

};


function App() {

 const [formData, setFormData] = useReducer(formReducer, {});

 const [submitting, setSubmitting] = useState(false);


 const handleSubmit = event => {

  event.preventDefault();

  setSubmitting(true);

  setTimeout(() => {

   setSubmitting(false);

  }, 7000); // Cambiado a 7000 para mostrar durante 7 segundos

 };


 const handleChange = event => {

  setFormData({

   name: event.target.name,

   value: event.target.value

  });

 };


 return (

  <div className="estilo">

   <h1>Usando formularios y eventos en React</h1>

   {submitting && (

    <div>

     <p>Realizaste Submit para la siguiente información:</p>

     <ul>

      {Object.entries(formData).map(([name, value]) => (

       <li key={name}>

        <strong>{name.charAt(0).toUpperCase() + name.slice(1)}</strong>: {value.toString()}

       </li>

      ))}

     </ul>

    </div>

   )}

   <form xx={handleSubmit}>

    <fieldset>

     <label>

      <p>Nombres</p>

      <input name="name" xx={handleChange} />

     </label>

     <label>

      <p>Apellidos</p>

      <input name="ape" xx={handleChange} />

     </label>

     <label>

      <p>Fecha de Nacimiento (DD/MM/YYYY)</p>

      <input name="birthdate" type="text" xx={handleChange} />

     </label>

     <label>

      <p>Dirección</p>

      <input name="address" xx={handleChange} />

     </label>

     <label>

      <p>Teléfono</p>

      <input name="phone" type="tel" xx={handleChange} />

     </label>

    </fieldset>

    <button type="submit">Submit</button>

   </form>

  </div>

 );

}


export default App;
